import React, { Component } from 'react';

class EditClient extends Component {
    constructor (props){
        super(props);

        this.state={
            formdata:{
                nama_client:'',
                _id : ''
            }
        };

        this.textHandler = this.textHandler.bind(this);
    }

    textHandler(e) {
        let tmp = this.state.formdata;
        tmp[e.target.name] = e.target.value;
        this.setState({
            formdata: tmp
        });
    }

    render(){
        return (
            <div className="modal-content">
                <div className="modal-header">
                    <button id="hidePopUpBtnUpdate" onClick = {this.resetForm} type="button" className="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">&times;</span></button>
                    <h4 className="modal-title">Edit Supplier</h4>
                </div>
                <form>
                    <div className="modal-body">
                        <div className="box-body">
                            <div className="form-group">
                                <label>Supplier Name</label>
                                <input ref="nama_client" type="text" className="form-control" placeholder="Client Name" 
                                name="nama_client" value={this.state.formdata.nama_client} onChange={this.textHandler} required/>
                                <span className="help-block" style={{color: "red"}}>{this.state.errors.err_nama_client}</span>
                            </div>
                        </div>
                    </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-default pull-left" onClick = {this.resetForm} data-dismiss="modal">Close</button>
                        <button type="button" className="btn btn-primary" onClick = {this.updateHandler}>Update</button>
                    </div>
                </form>
            </div>
        )
    };
};

export default EditClient;
